mapper.update:
update approval set
app_del='Y'
where app_id=#{app_id}

mapper.select:
	 select count(*) from approval
                where MEM_ID = #{mem_id}
                and app_time like '%'||#{app_time}||'%'

select * from (select a.*, rownum rn from (select app_id, app_pay, to_char(app_time,'YYYY-MM-DD HH24:MI:SS')app_time, app_type, mem_id
from approval where mem_id =#{mem_id}
and app_type =#{app_type}
order by app_time desc)a )b where rn >= (#{page}-1)*#{pageSize} + 1 and rn <= #{page}*#{pageSize}

	 select count(*) from approval
                where MEM_ID = #{mem_id}

		    	and app_type =#{app_type}
