mapper.update:
update category set
	cate_usage = 'N'
	where cate_id = #{cate_id} or cate_paerent_id =#{cate_id}

update category set
	cate_usage = 'N'
	where cate_id = #{cate_id}

update category set
	cate_usage = 'Y'
	where cate_id = #{cate_id} or cate_paerent_id =#{cate_id}

update category set
	cate_usage = 'Y'
	where cate_id = #{cate_id}

update category set
cate_title = #{cate_title}
where cate_id = #{cate_id}
