mapper.update:
UPDATE MEMBER
SET   
      mem_pass=#{mem_pass}

WHERE  mem_id = #{mem_id}

mapper.select:
SELECT mem_id
FROM MEMBER
WHERE mem_mail = #{mem_mail}
AND mem_nm = #{mem_nm}

mapper.update:
UPDATE member
SET
mem_del = #{mem_del}
WHERE mem_id = #{mem_id}
